import "./index.css";
import { useState, memo } from "react";
// import {useContext} from 'react'
// import { ThemeContext } from "../../App"

function Component({ placeholder, button, onSubmit }) {
  const [value, setValue] = useState("");
  const handleChange = (e) => setValue(e.target.value);
  const handleSubmit = () => {
    if (value === 0) return null;

    if (onSubmit) {
      onSubmit(value);
    } else {
      throw new Error("onSubmit props is undefined");
    }

    setValue("");
  };
  const isDisabled = value.length === 0;

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      if (e.ctrlKey || e.metaKey) {
        // Ctrl/Cmd + Enter — новий рядок
        e.preventDefault();
        const cursorPos = e.target.selectionStart;
        const newValue =
          value.substring(0, cursorPos) + "\n" + value.substring(cursorPos);
        setValue(newValue);
        // вручну оновлюємо позицію курсора після додавання \n
        setTimeout(() => {
          e.target.selectionStart = e.target.selectionEnd = cursorPos + 1;
        }, 0);
      } else {
        // Просто Enter — сабміт
        e.preventDefault();
        handleSubmit();
      }
    }
  };

  // const theme = useContext(ThemeContext)

  // console.log(theme)

  return (
    <div className="field-form">
      <textarea
        onChange={handleChange}
        value={value}
        rows={2}
        placeholder={placeholder}
        className="field-form__field"
        onKeyDown={handleKeyDown}
      ></textarea>
      <button
        disabled={isDisabled}
        onClick={handleSubmit}
        className="field-form__button"
      >
        {button}
      </button>
      {/* <button onClick={theme.toggle} className="field-form__button">Change theme</button> */}
    </div>
  );
}

export default memo(Component);
